mapper.insert:
insert into tb_goods(goods_name, goods_type, goods_price, count, brand_name, status)
values

    #{goods.goodsName},#{goods.goodsType}

mapper.select:
select *
from tb_goods


        and brand_name like #{brandName}


        and goods_name like #{goodsName}


        and goods_type like #{goodsType}


        and goods_price  >=  #{minPrice}


        and goods_price  <=  #{maxPrice}


        and status = #{status}

select *
from tb_goods
where


            brand_name like #{brandName}


            goods_name like #{goodsName}


            goods_type like #{goodsType}


            status = #{status}


            1 = 1;
